import logo from './logo.svg';
import {Login_signup} from "./login_signup/login_signup";
import {Home} from "./Home/home";
import 'bootstrap/dist/css/bootstrap.min.css'
import $ from 'jquery'
import 'bootstrap/dist/js/bootstrap.min.js'
import './lib/fonts/icomoon/style.css'
import './lib/global.css'
import Swal from "sweetalert2";
function App() {
  let a = localStorage.getItem('login_token')
  return (
    <div className="App">
        {a?<Home />:<Login_signup />}
    </div>
  );
}

export default App;
function showToast(message_string, type_string, position, styles, duration) {
  let messageText = (message_string) ? message_string : "متاسفانه مشکلی رخ داده است لطفا مجددا تلاش کنید.";
  let typeMessage = (type_string) ? type_string : "error";
  let position_end = (position) ? position : "bottom-start";
  let styles_main = (styles) ? styles : "";
  let timer = duration ? duration : 3000;

  const Toast = Swal.mixin({
    toast: true,
    position: position_end,
    showConfirmButton: false,
    timer: timer,
    timerProgressBar: true,
    didOpen: (toast) => {
      toast.onmouseenter = Swal.stopTimer;
      toast.onmouseleave = Swal.resumeTimer;
    },
    customClass: {
      popup: styles_main,
    }
  });
  Toast.fire({
    icon: typeMessage,
    title: messageText
  });
}
function showSwalMessage(message_string, type_string, show_button_boolean, duration_ms) {
  let messageText = (message_string) ? message_string : "متاسفانه مشکلی رخ داده است لطفا مجددا تلاش کنید.",
      typeMessage = (type_string) ? type_string : "error",
      buttonStatus = (show_button_boolean) ? true : false,
      duration = (duration_ms) ? duration_ms : 5000;
  Swal.fire({
    text: messageText,
    showClass: {
      popup: `animate__animated animate__fadeInUp animate__faster`
    },
    hideClass: {
      popup: `animate__animated animate__fadeOutDown animate__faster `
    },
    icon: typeMessage,
    confirmButtonText: "تایید",
    showConfirmButton: buttonStatus,
    timer: duration,
  })
  $("#swal2-html-container").css("direction", 'rtl')
}
